import React, {useState, Fragment} from "react";
import "src/asset/plugins/bootstrap/css/bootstrap.min.css";
import "src/asset/css/main.css";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit , faDeleteLeft} from '@fortawesome/free-solid-svg-icons'

import data from "src/views/Bed Booking/mock-data.json";
import ReadOnlyRow from "src/views/Bed Booking/ReadOnlyRow";
import EditableRow from "src/views/Bed Booking/EditableRow";
import { nanoid } from "nanoid";


const All_bed=()=>{
    const [contacts, setContacts] = useState(data);
    const [addFormData, setAddFormData] = useState({
        Room_No: "",
        Patient: "",
        Allotment: "",
        Discharge: "",
        Room_Type: ""


    });

    const [editFormData, setEditFormData] = useState({
        Room_No: "",
        Patient: "",
        Allotment: "",
        Discharge: "",
        Room_Type: ""
        
    });

    const [editContactId, setEditContactId] = useState(null);

    const handleAddFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...addFormData };
        newFormData[fieldName] = fieldValue;

        setAddFormData(newFormData);
    };

    const handleEditFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...editFormData };
        newFormData[fieldName] = fieldValue;

        setEditFormData(newFormData);
    };

    const handleAddFormSubmit = (event) => {
        event.preventDefault()
        const newContact = {
            id: nanoid(),
            Doctor_Name: addFormData.Doctor_Name,
            Department: addFormData.Department,
            AvailableDys: addFormData.AvailableDys,
            AvailableTime: addFormData.AvailableTime,

        };
        const newContacts = [...contacts, newContact];
        setContacts(newContacts);
    };

    const handleEditFormSubmit = (event) => {
        event.preventDefault();

        const editedContact = {
            id: editContactId,
            Doctor_Name: editFormData.Doctor_Name,
            Department: editFormData.Department,
            AvailableDys: editFormData.AvailableDys,
            AvailableTime: editFormData.AvailableTime,

        };

        const newContacts = [...contacts];
        const index = contacts.findIndex((contact) => contact.id === editContactId);

        newContacts[index] = editedContact;

        setContacts(newContacts);
        setEditContactId(null);
    };

    const handleEditClick = (event, contact) => {
        event.preventDefault();
        setEditContactId(contact.id);
        const formValues = {
            Doctor_Name: contact.Doctor_Name,
            Department: contact.Department,
            AvailableDys: contact.AvailableDys,
            AvailableTime: contact.AvailableTime,

        };

        setEditFormData(formValues);
    };

    const handleCancelClick = () => {
        setEditContactId(null);
    };

    const handleDeleteClick = (contactId) => {
        const newContacts = [...contacts];
        const index = contacts.findIndex((contact) => contact.id === contactId);
        newContacts.splice(index, 1);
        setContacts(newContacts);
    };

    
    return(
        <>
        <div>
        <section class="content">
                    <div class="container-fluid">
                        <div class="block-header">
                            <h2>All bed</h2>
                            <small class="text-muted">Welcome to bed booking</small>
                        </div>
                        <div class="row clearfix">
                            <div class="col-lg-12 col-md-12 col-sm-12">
                                <div class="card">
                                    <div class="header">
                                    <div class="col row-12 d-flex">
                                <h2 class="col-10">Departments</h2>
                                <Link to="Add bed">
                                <button class="col-2" type="button" class="btn btn-primary">Add Now</button>
                                </Link>
                                  </div>
                                       
                                    </div>
                                    <div class="body table-responsive">
                                    <form onSubmit={handleEditFormSubmit}>
                                        <table class="table table-bordered table-striped table-hover js-basic-example dataTable">
                                            <thead>
                                                <tr>
                                                    <th>Room No</th>
                                                    <th>Patient</th>
                                                    <th>Allotment</th>
                                                    <th>Discharge</th>
                                                    <th>Room Type</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            {/*<tfoot>
                                                <tr>
                                                    <th>Name</th>
                                                    <th>Position</th>
                                                    <th>Office</th>
                                                    <th>Age</th>
                                                    <th>Start date</th>
                                                    <th>Salary</th>
                                                </tr>
                                            </tfoot>*/}
                                            <tbody>
                                            {contacts.map((contact) => (
                                                        <Fragment>
                                                            {editContactId === contact.id ? (
                                                                <EditableRow
                                                                    editFormData={editFormData}
                                                                    handleEditFormChange={handleEditFormChange}
                                                                    handleCancelClick={handleCancelClick}
                                                                />
                                                            ) : (
                                                                <ReadOnlyRow
                                                                    contact={contact}
                                                                    handleEditClick={handleEditClick}
                                                                    handleDeleteClick={handleDeleteClick}
                                                                />
                                                            )}
                                                        </Fragment>
                                                    ))}
                                               
                                               
                                               
                                                
        
                                            </tbody>
                                        </table>
                                        </form>
                                        <form onSubmit={handleAddFormSubmit}>
                                            <input
                                                type="text"
                                                name="Room_No"
                                                required="required"
                                                placeholder="Enter Room_no"
                                                onChange={handleAddFormChange}
                                            />
                                            <input
                                                type="text"
                                                name="Patient"
                                                required="required"
                                                placeholder="Patient"
                                                onChange={handleAddFormChange}
                                            />
                                             <input
                                                type="text"
                                                name="Allotment"
                                                required="required"
                                                placeholder="Allotment"
                                                onChange={handleAddFormChange}
                                            />
                                             <input
                                                type="text"
                                                name="Dischage"
                                                required="required"
                                                placeholder="Discharge"
                                                onChange={handleAddFormChange}
                                            />
                                            <input
                                                type="text"
                                                name="Room_Type"
                                                required="required"
                                                placeholder="Discharge"
                                                onChange={handleAddFormChange}
                                            />
                                            <button className="btn btn-primary" type="submit">Add</button>
                                        </form>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
        </div>


        </>
    )
}
export default All_bed;
