import React from "react";
import "src/asset/plugins/bootstrap/css/bootstrap.min.css";
import "src/asset/css/main.css";

const Add_Department = () => {
    const initialValues = {departmentname: " ", 
    
};
  const [formValues, setformValues] = useState(initialValues);
  const [formErrors, setformErrors] = useState({});
  const [IsSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) =>{
    const {name, value} = e.target;
    setformValues({...formValues, [name]: value});
  };
  const handleSubmit = (e)=>{
    console.log(handleSubmit);
    console.log(setIsSubmit);
    e.preventDefault();
    (setformErrors(validate(formValues)))
    
    setIsSubmit(true);
  }
  useEffect(()=>{
    console.log(IsSubmit);
    if(Object.keys(formErrors).length === 0 && IsSubmit){
      console.log(formErrors);
    }   
  }, [formErrors])


  const validate=(values)=>{
   
    const regex = /^[^\s@[^\s@+\.[^\s@]{2,}$/i;
    const errors={}
    if(values.firstname ==0){
      errors.firstname = "firstname is required";
    }
    if(values.lastname == 0){
      errors.lastname = "lastname is required";
    }
    
    if(values.specialise == 0){
        errors.specialise = "specialise is required";
    }
    if(values.phone == 0){
        errors.phone = "phone is required";
    }
    if(values.website == 0){
        errors.website = "phone is required";
    }
    if(values.email == 0){
        errors.email = "email is required";
    }else if (!regex.text(values.email ==0)){
        errors.email =" this is not valid email";
  
      }
    
    return errors;
    
  }

    return (

        <>
            <div>
                <section className="content">
                    <div className="container-fluid">
                        <div className="block-header">
                            <h2>Add department</h2>

                        </div>
                        <div className="row clearfix">
                            <div className="col-lg-12 col-md-12 col-sm-12 ">
                                <div className="card">
                                    <div className="header">
                                        <h2>add department  </h2>

                                    </div>
                                    <div className="body">
                                        <div className="row clearfix">
                                            <div className="col-sm-12 ">
                                                <div className="form-group1">
                                                    <div className="form-line1">
                                                        <input type="text" className="form-control" placeholder="Add department name" />
                                                    </div>
                                                </div>
                                            </div>

                                        </div>

                                        <div className="row clearfix">
                                            <div className="col-sm-12">
                                                <div className="form-group1">
                                                    <div className="form-line1">
                                                        <textarea rows={4} className="form-control no-resize" placeholder="description" defaultValue={""} />
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row-sm-12  d-flex">
                                                <div class=" col-8 form-group1">
                                                    <div >
                                                        <div>
                                                            <label class="display-block">Department Status</label>
                                                        </div>
                                                        <div class="form-check form-check-inline">
                                                            <input class="form-check-input" type="radio" name="status" id="product_active" value="option1"  />
                                                            <label class="form-check-label" for="product_active">Active</label>
                                                        </div>
                                                    </div>
                                                    <div class="form-check form-check-inline">
                                                        <input class="form-check-input" type="radio" name="status" id="product_inactive" value="option2" checked="" />
                                                        <label class="form-check-label" for="product_inactive">Inactive</label>
                                                    </div>
                                                </div>

                                                

                                             </div>
                                             <div className="col-sm-12  text align-center">
                                                <button  type="submit" className="btn btn-raised g-bg-cyan">Create Department</button>
                                                </div>
                                            

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

            </div>


        </>
    );
};
export default Add_Department;
