import React from "react";
import "src/asset/plugins/bootstrap/css/bootstrap.min.css";
import "src/asset/css/main.css";
import Select from 'react-select'
/*import "../../../asset/plugins/bootstrap/css/bootstrap.min.css";
import "../../../asset/css/main.css";*/


const Add_payment = () => {
    const initialValues = {paymentno: " ", patientname:" ", fullname:"", gender: "", specialise: "", phone:"", email: "",
    website: "", description: ""
};
  const [formValues, setformValues] = useState(initialValues);
  const [formErrors, setformErrors] = useState({});
  const [IsSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) =>{
    const {name, value} = e.target;
    setformValues({...formValues, [name]: value});
  };
  const handleSubmit = (e)=>{
    console.log(handleSubmit);
    console.log(setIsSubmit);
    e.preventDefault();
    (setformErrors(validate(formValues)))
    
    setIsSubmit(true);
  }
  useEffect(()=>{
    console.log(IsSubmit);
    if(Object.keys(formErrors).length === 0 && IsSubmit){
      console.log(formErrors);
    }   
  }, [formErrors])


  const validate=(values)=>{
   
    const regex = /^[^\s@[^\s@+\.[^\s@]{2,}$/i;
    const errors={}
    if(values.firstname ==0){
      errors.firstname = "firstname is required";
    }
    if(values.lastname == 0){
      errors.lastname = "lastname is required";
    }
    
    if(values.specialise == 0){
        errors.specialise = "specialise is required";
    }
    if(values.phone == 0){
        errors.phone = "phone is required";
    }
    if(values.website == 0){
        errors.website = "phone is required";
    }
    if(values.email == 0){
        errors.email = "email is required";
    }else if (!regex.text(values.email ==0)){
        errors.email =" this is not valid email";
  
      }
    
    return errors;
    
  }



    const option = [
        { value: 'Dr. Aman Dubey', label: 'Dr. Aman Dubey' },
        { value: 'Dr. Rajiv', label: 'Dr. Rajiv' },
        { value: 'Dr. Rajiv sharma', label: 'Dr. Rajiv Sharma' },
        { value: 'Dr. Rajiv', label: 'Dr. Rajiv' },
    ]
    const options = [
        { value: 'January', label: 'Janaury' },
        { value: 'Feb', label: 'Feb' },
        { value: 'March', label: 'March' },
        { value: 'April', label: 'April' },
        { value: 'May', label: 'May' },
        { value: 'June', label: 'June' },
        { value: 'July', label: 'July' },
        { value: 'August', label: 'August' },
        { value: 'September', label: 'September' },
        { value: 'Oct', label: 'Oct' },
        { value: 'November', label: 'November' },
        { value: 'December', label: 'December' },
    ]
    return (
        <>
            <div>
                <section className="content">
                    <div className="container-fluid">
                        <div className="block-header">
                            <h2>Add Payment</h2>
                            <small className="text-muted">Welcome to Addpayment</small>
                        </div>
                        <div className="row clearfix">
                            <div className="col-lg-12 col-md-12 col-sm-12">
                                <div className="card">
                                    <div className="header">
                                        <h2>Payment Information  </h2>
                                        <button type="button" class="btn btn-primary">Edit</button>

                                    </div>
                                    <div className="body">

                                        <div className="row clearfix ">

                                            <div className="col-sm-6 ">

                                                <lable>
                                                    Envoice Number
                                                </lable>
                                                <div className="form-group1">
                                                    <div className="form-line">
                                                        <input type="number" className="form-control" placeholder="Payment Number" />
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="col-sm-6">
                                                <lable>Patient Name</lable>
                                                <div className="form-group1">
                                                    <div className="form-line">
                                                        <input type="text" className="form-control" placeholder="Patient Name" />
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="col-sm-6">
                                                <lable>Doctor</lable>
                                                <div className="form-group1">
                                                    <div className="form-line">
                                                        <Select options={option} />
                                                    </div>
                                                </div>
                                            </div>
